// src/components/QuizPage.js
import React, { useState } from 'react';
import quizData from '../quizData';
import { useNavigate } from 'react-router-dom';

function QuizPage() {
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [score, setScore] = useState(0);
    const [showScore, setShowScore] = useState(false);
    const [selectedOptions, setSelectedOptions] = useState({});
    const navigate = useNavigate();

    const handleOptionClick = (option) => {
        setSelectedOptions({ ...selectedOptions, [currentQuestion]: option });
    };

    const handleNextQuestion = () => {
        if (quizData[currentQuestion].answer === selectedOptions[currentQuestion]) {
            setScore(score + 1);
        }

        const nextQuestion = currentQuestion + 1;
        if (nextQuestion < quizData.length) {
            setCurrentQuestion(nextQuestion);
        } else {
            setShowScore(true);
        }
    };

    const handlePlayAgain = () => {
        setCurrentQuestion(0);
        setScore(0);
        setShowScore(false);
        setSelectedOptions({});
    };

    return (
        <div className="quiz-page">
            {showScore ? (
                <div className="score-section">
                    <p>You have scored {score} out of {quizData.length}</p>
                    <button onClick={handlePlayAgain}>Play Again</button>
                </div>
            ) : (
                <div className="question-section">
                    <h2>{quizData[currentQuestion].question}</h2>
                    <div className="options">
                        {quizData[currentQuestion].options.map((option, index) => (
                            <button
                                key={index}
                                onClick={() => handleOptionClick(option)}
                                className={selectedOptions[currentQuestion] === option ? 'selected' : ''}
                            >
                                {option}
                            </button>
                        ))}
                    </div>
                    <button onClick={handleNextQuestion}>Next</button>
                </div>
            )}
        </div>
    );
}

export default QuizPage;
